import React, { Component } from 'react';
import Cover from '../components/Home/Cover';
import Experiences from '../components/Home/Experiences';
import Navbar from '../components/General/Navbar';
import Projects from '../components/Home/Projects';
import Footer from '../components/General/Projects';

export default class Home extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
		};
		this.experiences = React.createRef();
		this.projects = React.createRef();
	}

	startAtTop = () => window.scrollTo(0, 0);

	jumpToExperiences = () => window.scrollTo(0, this.experiences.current.offsetTop - 20);

	jumpToProjects = () => window.scrollTo(0, this.projects.current.offsetTop);

	componentDidMount() {
		this.startAtTop();
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);
		window.addEventListener('scroll', this.listenScrollEvent);
	}

	render() {
		return (
			<div id="home">
				<Navbar jump={this.jumpToExperiences} jump2={this.jumpToProjects} />
				<Cover jump={this.jumpToExperiences} />
				<Experiences experiences={this.experiences} />
				<Projects projects={this.projects} />
				<Footer />
			</div>
		);
	}
}
