import React, { Component } from 'react';
import Cover from '../components/Home/Cover';
import Experiences from '../components/Home/Experiences';
import Navbar from '../components/General/Navbar';
import Projects from '../components/Home/Projects';
import Footer from '../components/General/Footer';

export default class Home extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
			didScroll: false,
			lastScrollTop: 0,
			delta: 5,
			navbarHeight: 94,
			scrollUp
		};
		this.top = React.createRef();
		this.experiences = React.createRef();
		this.projects = React.createRef();
	}

	startAtTop = () => window.scrollTo(0, this.top.current.offsetTop);

	jumpToExperiences = () => window.scrollTo(0, this.experiences.current.offsetTop - 20);

	jumpToProjects = () => window.scrollTo(0, this.projects.current.offsetTop);

	componentDidMount() {
		this.startAtTop();
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);
		this.setState(this.props.location.state);
		window.addEventListener('scroll', this.listenScrollEvent);
	}

	listenScrollEvent = e => {
		this.setState({didScroll: true});
		console.log('I scrolled')
		let scrollTop = e.target.scrollingElement.scrollTop,
		opacity = scrollTop/400, lastScrollTop = this.state.lastScrollTop;

    // Make sure they scroll more than delta
    if(Math.abs(lastScrollTop - scrollTop) <= this.state.delta)
        return;

    // If they scrolled down and are past the navbar, add class .nav-up.
    // This is necessary so you never see what is "behind" the navbar.
    if (st > lastScrollTop && scrollTop > this.state.navbarHeight){
        // Scroll Down
        $('header').removeClass('nav-down').addClass('nav-up');
    } else {
        // Scroll Up
        if(scrollTop + $(window).height() < $(document).height()) {
            $('header').removeClass('nav-up').addClass('nav-down');
        }
    }

    this.setState({lastScrollTop: scrollTop});

/*     let colorCode = 255-(opacity*531);

    if (opacity > 0.48) {
      this.setState({color: '#1d1d1d', boxShadow: '0 0 10px 0 rgba(0,0,0,.16)'})
    } else {
      this.setState({color: `rgb(${colorCode},${colorCode},${colorCode})`, boxShadow: 'none'})
    } 

    this.setState({
      opacity: `rgb(255,255,255,${opacity})`
		});  */

		console.log(scrollTop)
  }

	render() {
		return (
			<div id="home">
				<Navbar jump={this.jumpToExperiences} jump2={this.jumpToProjects} top={this.top} scrollUp={this.state.scrollUp}/>
				<Cover jump={this.jumpToExperiences} />
				<Experiences experiences={this.experiences} />
				<Projects projects={this.projects} />
				<Footer />
			</div>
		);
	}
}
