import React, { Component } from 'react';

class Navbar extends Component {
  constructor(props) {
		super(props);
		this.state = {
      hover: false,
    };
  }

  toggleHover = (x) => {
    this.setState({hover: !this.state.hover}, () => this.color(x))
  }

  color = (x) => {
    let hoverStyle = this.props.hoverColor
    if (this.state.hover) {
      if (x === "experiences") {
        this.setState({hoverExperiences: hoverStyle})
      } else if (x === "projects") {
        this.setState({hoverProjects: hoverStyle})
      }
    } else {
      this.setState({hoverExperiences: this.props.hoverMenu, hoverProjects: this.props.hoverMenu})
    }
  }


  render() {
    return (
      <div id="navbar" className={`${this.props.scrollUp && 'scrollUp'}`} ref={this.props.top}>
        <h3 style={{color: this.props.colorTitle}} onClick={() => this.props.main('main')} onMouseOver={() => this.toggleHover('main')}
			onMouseLeave={() => this.toggleHover('main')}>Sarah Alt</h3>
        <div className="navbar_sections">
          <p onClick={this.props.jump} onMouseEnter={() => this.toggleHover('experiences')} style={{color: this.state.hoverExperiences}}
			onMouseLeave={() => this.toggleHover('experiences')}>Experiences</p>
          <p onClick={this.props.jump2} style={{color: this.state.hoverProjects}} onMouseEnter={() => this.toggleHover('projects')}
			onMouseLeave={() => this.toggleHover('projects')}>Projects</p>
          {(this.props.showContact) ? (<p onClick={() => this.props.contact()}onMouseEnter={() => this.toggleHover('contact')}
			onMouseLeave={() => this.toggleHover('contact')}>Contact</p>) : null}
        </div>
      </div>
    );
  }
}

export default Navbar;
