import React, { Component } from 'react';
import IntroSlides from './IntroSlides';
import Scroll from '../General/Scroll';
import {INTRO} from '../../data/Intro';

class Cover extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
      time: 300,
      slide: 0,
		};
	}

	componentDidMount() {
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);

		this.myInterval = setInterval(() => {
			this.setState(({ time }) => ({
				time: time - 1,
			}));
			if (this.state.time === 0 || this.state.time === -300) {
        this.setState({slide: this.state.slide + 1})
			} else if (this.state.time === -300) {
        this.setState({slide: this.state.slide + 1})
        clearInterval(this.myInterval);
			}
		}, 30);
  }

  componentWillUnmount() {
    clearInterval(this.myInterval)
}

	render() {
    console.log(this.state.time, this.state.slide)
		return (
			<div id="cover">
				<IntroSlides time={this.state.time} intro={INTRO[this.state.slide]}/>
				<Scroll jump={this.props.jump} />
			</div>
		);
	}
}

export default Cover;
