import React, { Component } from 'react';

import { PROJECTS } from '../../data/Projects';
import Box from '../General/Box';
import ExpandedBox from '../General/ExpandedBox';

export default class Projects extends Component {
	constructor(props) {
		super(props);
		this.state = {
      openProject: 1,
      firstItems: PROJECTS,
      lastItems: {},
		};
	}

	render() {
		return (
			<div id="projects" ref={this.props.projects}>
				<div className="projects_overview">
					<div className="projects_overview_title">
						<h2>My Projects</h2>
					</div>
					{PROJECTS.map((eachProject, index) => {
						return (
								<Box item={eachProject} />
						);
					})}
					{this.state.openProject != 0 ? <ExpandedBox item={PROJECTS[this.state.openProject]} /> : <p></p>}
				</div>
			</div>
		);
	}
}
