import React, { Component } from 'react';
import IntroSlides from './IntroSlides';
import Scroll from '../General/Scroll';

class Cover extends Component {
	constructor(props) {
		super(props);
		this.state = {
      didMount: false,
      time: 300,
		};
	}

	componentDidMount() {
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);

		this.myInterval = setInterval(() => {
			this.setState(({ time }) => ({
				time: time - 1,
      }));

      if (seconds === 0) {
        if (minutes === 0) {
          clearInterval(this.myInterval)
        } else {
          this.setState(({ minutes }) => ({
            minutes: minutes - 1,
            seconds: 59
          }))
        }
      }

		}, 30);
	}

	render() {
		return (
			<div id="cover">
				<IntroSlides time={this.state.time} />
				<Scroll jump={this.props.jump} />
			</div>
		);
	}
}

export default Cover;
