import React, { Component } from 'react';
import { ComposableMap, Geographies, Geography, ZoomableGroup, Marker } from 'react-simple-maps';

const geoUrl = 'https://raw.githubusercontent.com/seabysalt/maps/master/maps.json';

const markers = [
  {
    markerOffset: -30,
    name: "San Francisco",
    coordinates: [-122.4312, 37.7739]
  },
  { markerOffset: 15, name: "Houston", coordinates: [-95.3584, 29.7499] },
  { markerOffset: 15, name: "Lyon", coordinates: [4.8467, 45.7485] },
  { markerOffset: 15, name: "Salamanca", coordinates: [-5.6638, 40.9688] },
  { markerOffset: 15, name: "Munich", coordinates: [11.5761, 48.1371] },
  { markerOffset: 15, name: "Dodoma", coordinates: [35.7454, -6.1611] },
  { markerOffset: 15, name: "London", coordinates: [-0.1180, 51.5098] },
  { markerOffset: 15, name: "Quito", coordinates: [-78.4678, -0.1806] },
];

export default class Map extends Component {

  seeExperience = (name) => {
    console.log(name)
  }

	render() {
		return (
			<div className="map_detail">
				<ComposableMap viewBox="0 0 600 600">
					<Geographies geography={geoUrl} fill="#D6D6DA" stroke="#FFFFFF" strokeWidth={0.5}>
						{({ geographies }) => geographies.map((geo) => <Geography key={geo.rsmKey} geography={geo} />)}
					</Geographies>
					{markers.map(({ name, coordinates, markerOffset }) => (
        <Marker key={name} coordinates={coordinates} onClick={this.seeExperience(name)}>
          <g
            fill="none"
            stroke="#FF5533"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
            transform="translate(-12, -24)"
          >
            <circle cx="12" cy="10" r="3" />
            <path d="M12 21.7C17.3 17 20 13 20 10a8 8 0 1 0-16 0c0 3 2.7 6.9 8 11.7z" />
          </g>
{/*           <text
            textAnchor="middle"
            y={markerOffset}
            style={{ fontFamily: "system-ui", fill: "#5D5A6D" }}
          >
            {name}
          </text> */}
        </Marker>
        ))}
				</ComposableMap>
			</div>
		);
	}
}
