import React, { Component } from 'react';

class Navbar extends Component {
  constructor(props) {
		super(props);
		this.state = {
      hover: false,
    };
    this.startAgain = this.startAgain.bind(this)
  }

  toggleHover = (x) => {
    this.setState({hover: !this.state.hover}, () => this.color(x))
  }

  color = (x) => {
    let hoverStyle = ""
    if (this.state.hover) {
      if (x === "experiences") {
        hoverStyle
      }
    }
  }


  render() {
    return (
      <div id="navbar" className={`${this.props.scrollUp && 'scrollUp'}`} ref={this.props.top}>
        <h3 style={{color: this.props.colorTitle}} onClick={() => this.props.main('main')} onMouseEnter={this.toggleHover('main')}
			onMouseLeave={this.toggleHover('main')}>Sarah Alt</h3>
        <div className="navbar_sections" style={{color: this.props.colorMenu}}>
          <p onClick={this.props.jump} onMouseEnter={this.toggleHover('experiences')}
			onMouseLeave={this.toggleHover('experiences')}>Experiences</p>
          <p onClick={this.props.jump2} onMouseEnter={this.toggleHover('projects')}
			onMouseLeave={this.toggleHover('projects')}>Projects</p>
          {(this.props.showContact) ? (<p onClick={() => this.props.contact()}onMouseEnter={this.toggleHover('contact')}
			onMouseLeave={this.toggleHover('contact')}>Contact</p>) : null}
        </div>
      </div>
    );
  }
}

export default Navbar;
