import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import Navbar from '../components/General/Navbar';
import Footer from '../components/General/Footer';

class Contact extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
			lastScrollTop: 0,
			scrollUp: false,
			width: 0,
			height: 0,
		};
		this.top = React.createRef();
		window.addEventListener('resize', this.update);
	}

	componentDidMount() {
		this.startAtTop();
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);
		this.setState(this.props.location.state);
		window.addEventListener('scroll', this.listenScrollEvent);
		this.update();
	}

	startAtTop = () => window.scrollTo(0, this.top.current.offsetTop);

	listenScrollEvent = (e) => {
		let scrollTop = e.target.scrollingElement.scrollTop,
			lastScrollTop = this.state.lastScrollTop;

		if (scrollTop < lastScrollTop && scrollTop > 94 && !this.state.scrollUp) {
			this.setState({ scrollUp: true, lastScrollTop: scrollTop });
		} else if (scrollTop > lastScrollTop && this.state.scrollUp) {
			this.setState({ scrollUp: false, lastScrollTop: scrollTop });
		} else if (scrollTop < lastScrollTop && scrollTop < 1 && this.state.scrollUp) {
			this.setState({ scrollUp: false, lastScrollTop: scrollTop });
		} else {
			this.setState({ lastScrollTop: scrollTop });
		}
	};

	update = () => {
		this.setState({
			width: window.innerWidth,
			height: window.innerHeight,
		});
	};

	jumpToExperiences = () => {
		this.props.history.push({
			pathname: '/',
			state: { loadOnMount: 'experiences' },
		});
	};

	jumpToProjects = () => {
		this.props.history.push({
			pathname: '/',
			state: { loadOnMount: 'projects' },
		});
	};

	main = () => {
		this.props.history.push({
			pathname: '/',
			state: { loadOnMount: 'top' },
		});
  };

  contact = () => {
    this.startAtTop()
  }

	render() {
		return (
			<div id="home">
				<Navbar
					jump={this.jumpToExperiences}
					jump2={this.jumpToProjects}
					top={this.top}
					scrollUp={this.state.scrollUp}
					showContact={false}
					main={() => this.main()}
					colorTitle="#333333"
          colorMenu="#333333"
          hoverColor="#9BA59C"
				/>
				<div className="home_content contact">
					<div className="contact_details">
						<div className="contact_background">
							<h2>Let's stay in touch!</h2>
							<div className="contact_form">
								<form action="Contact" method="post">
									<label htmlFor="">Your name</label>
									<input type="text" name="name" id="1" />									<label htmlFor="">Your email</label>
									<input type="email" name="email" id="2" />
									<label htmlFor="">Your message</label>
									<textarea className="message" type="text" /* rows="7"  */name="message" id="3" />

								</form>
								<button type="submit">Share with me!</button>
							</div>
						</div>
					</div>
					<img src="/images/Touch4.jpg" alt="" />
				</div>
				<Footer contact={this.contact}/>
			</div>
		);
	}
}

export default withRouter(Contact);
