import React, { Component } from 'react';
import Map from './Map';
import { EXPERIENCES } from '../../data/Experiences';

class Experiences extends Component {
	constructor(props) {
		super(props);
		this.state = {
			firstMount: true,
			experience: [],
      country: '',
      countries: ['Germany', 'Tanzania', 'France', 'Spain', 'USA', 'UK', 'Ecuador'],
		};
		this.seeExperience = this.seeExperience.bind(this);
	}

	seeExperience(name) {
		let experience = [];
		if (name === 'Houston' || name === 'San Francisco') {
			EXPERIENCES.forEach((eachExperience) => {
				if (eachExperience.country === 'USA') experience.push(eachExperience);
			});
			this.setState({ experience: experience, firstMount: false, country: 'USA' });
		} else {
			EXPERIENCES.forEach((eachExperience) => {
				if (eachExperience.country === name) experience.push(eachExperience);
			});
			this.setState(
				{ experience: experience, firstMount: false, country: name },
				console.log(this.state.country)
			);
		}
	}

	render() {
    const experience = this.state.experience;
    const countries = this.state.countries;
		return (
			<div id="experiences" ref={this.props.experiences}>
				<div className="map">
					<div className="map_countries">
          {countries.map((eachCountry, index) => {
								return (
									<p
							className={`${this.state.country === eachCountry ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience(eachCountry)}
						>
							{eachCountry}
						</p>
								);
							})}
					)}
						<p
							className={`${this.state.country === 'Germany' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('Germany')}
						>
							Germany
						</p>
						<p
							className={`${this.state.country === 'Tanzania' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('Tanzania')}
						>
							Tanzania
						</p>
						<p
							className={`${this.state.country === 'France' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('France')}
						>
							France
						</p>
						<p
							className={`${this.state.country === 'Spain' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('Spain')}
						>
							Spain
						</p>
						<p
							className={`${this.state.country === 'USA' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('USA')}
						>
							USA
						</p>
						<p
							className={`${this.state.country === 'UK' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('UK')}
						>
							UK
						</p>
						<p
							className={`${this.state.country === 'Ecuador' ? 'active' : 'inactive'}`}
							onClick={() => this.seeExperience('Ecuador')}
						>
							Ecuador
						</p>
					</div>
					<Map seeExperience={this.seeExperience} />
				</div>
				<div className="text">
					{this.state.firstMount ? (
						<h2>Click on the countries to get to know me</h2>
					) : (
						<div className="experience">
							<h2>{this.state.country}</h2>
							{experience.map((eachExperience, index) => {
								return (
									<div className="experience_details">
										<div className="experience_details_year">{eachExperience.year}</div>
										<div className="experience_details_institution">
											{eachExperience.institution}
										</div>
										<div className="experience_details_title">{eachExperience.title}</div>
									</div>
								);
							})}
							;
						</div>
					)}
				</div>
			</div>
		);
	}
}

export default Experiences;
