import React, { Component } from 'react';
import Arrow_Spinner from '../General/Arrow_Spinner';
import {INTRO} from '../../data/Intro';

class IntroSlides extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
			time: 300,
			slide2: false,
			slide3: false,
		};
		this.next = this.next.bind(this);
	}

	componentDidMount() {
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);

		this.myInterval = setInterval(() => {
			this.setState(({ time }) => ({
				time: time - 1,
			}));
			if (this.state.time === 0 && this.state.slide3) {
				clearInterval(this.myInterval);
			}
			if (this.state.time === 0) {
				this.setState({ slide2: true });
			}
			if (this.state.time === -300) {
				this.setState({ slide3: true });
			} else if (this.state.time === -605) {
				clearInterval(this.myInterval);
			}
		}, 30);
	}

	componentDidUpdate() {
		if (this.state.slide2) {
			this.myInterval = setInterval(() => {
				this.setState(({ time }) => ({
					time: time - 1,
				}));
				if (this.state.time === 0 && this.state.slide3) {
					clearInterval(this.myInterval);
				}
				if (this.state.time === 0) {
					this.setState({ slide2: true });
				}
				if (this.state.time === -300) {
					this.setState({ slide3: true });
				} else if (this.state.time === -605) {
					clearInterval(this.myInterval);
				}
			}, 30);
		} else if (this.state.slide3) {

		}
	}

	componentWillUnmount() {
		clearInterval(this.myInterval);
	}

	next() {
		if (!this.state.slide2 && !this.state.slide3) {
			this.setState({ slide2: !this.state.slide2 })
		} else if (this.state.slide2 && !this.state.slide3) {
			this.setState({ slide3: !this.state.slide3 })
		} 
	}

	render() {
		let intro;
		if (!this.state.slide2 && !this.state.slide3) {
			intro = INTRO[0]
		} else if (this.state.slide2 && !this.state.slide3) {
			intro = INTRO[1]
		} else if (this.state.slide3) {
			intro = INTRO[2]
		}

		return (
			<div id="introSlides">
				<div className="background">
					<div
						className={`${!this.state.slide3 && 'background_fade-in'} background_color`}
						style={{ backgroundColor: INTRO[0].color }}
					></div>
					<div
						className={`${this.state.slide2 && 'background_fade-in'} background_color`}
						style={{ backgroundColor: INTRO[1].color }}
					></div>
					<div
						className={`${this.state.slide3 && 'background_fade-in'} background_color`}
						style={{ backgroundColor: INTRO[2].color }}
					></div>
				</div>
				{this.state.slide2 && !this.state.slide3 ? (
					<div className="text2">
						<h5 className="tracking-in-expand-fwd-top">{intro.subtitle}.</h5>
						<h1 className="tracking-in-expand-fwd-top">{intro.title}</h1>
					</div>
				) : (
					<div className="text">
						<h5 className="tracking-in-expand-fwd-bottom">{intro.subtitle}.</h5>
						<h1 className="tracking-in-expand-fwd-bottom">{intro.title}</h1>
					</div>
				)}
				<div className="image">
					<div className={`${this.state.slide2 && 'images2'} images ${this.state.slide3 && 'images3'}`}>
						<img
							className={`${!this.state.slide3 && 'image_fade-in'} image_fade-in image_sliding`}
							src={INTRO[0].imageUrl}
							alt="Profile_Picture"
						/>
						<img
							className={`${this.state.slide2 && 'image_fade-in-2'} image_fade-in-2 image_sliding`}
							src={INTRO[1].imageUrl}
							alt="Profile_Picture"
						/>
						<img
							className={`${this.state.slide3 && 'image_fade-in-3'} image_fade-in-3 image_sliding`}
							src={INTRO[2].imageUrl}
							alt="Profile_Picture"
						/>
					</div>
					<div className="image_control">
						<div className="image_control_text">
							<p>0{intro.id}</p>
							<p>/</p>
							<p>03</p>
						</div>
						<Arrow_Spinner time={this.state.time} color={intro.color} next={this.next}/>
					</div>
				</div>
			</div>
		);
	}
}

export default IntroSlides;
