import React, { Component } from 'react';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

const geoUrl = 'https://raw.githubusercontent.com/seabysalt/maps/master/maps.json';

const markers = [
	{
		markerOffset: 15,
		name: 'San Francisco',
		coordinates: [-122.4312, 37.7739],
	},
	{ markerOffset: 15, name: 'Houston', coordinates: [-95.3584, 29.7499] },
	{ markerOffset: 15, name: 'France', coordinates: [4.8467, 45.7485] },
	{ markerOffset: 15, name: 'Spain', coordinates: [-5.6638, 40.9688] },
	{ markerOffset: 15, name: 'Germany', coordinates: [11.5761, 48.1371] },
	{ markerOffset: 15, name: 'Tanzania', coordinates: [35.7454, -6.1611] },
	{ markerOffset: 15, name: 'UK', coordinates: [-0.118, 51.5098] },
	{ markerOffset: 15, name: 'Ecuador', coordinates: [-78.4678, -0.1806] },
];

export default class Map extends Component {
	seeExperience(coordinates) {
		console.log(coordinates);
	}

	render() {
		const country = this.props.country;
		return (
			<div className="map_detail">
				<ComposableMap viewBox="50 0 550 600">
					<Geographies geography={geoUrl} fill="#D6D6DA" stroke="#FFFFFF" strokeWidth={0.5}>
						{({ geographies }) => geographies.map((geo) => <Geography key={geo.rsmKey} geography={geo} />)}
					</Geographies>
					{markers.map(({ name, coordinates, markerOffset }) => (
						<Marker key={name} coordinates={coordinates} onClick={() => this.props.seeExperience(name)}>
							<g
								fill="none"
								stroke={`${country === name ? '#ff5b3e' : '#0a0a0a'}`}
								strokeWidth="2"
								strokeLinecap="round"
								strokeLinejoin="round"
								transform="translate(-12, -24)"
							>
								{country === name ? <circle cx="12" cy="6" r="4" /> : <circle cx="12" cy="10" r="3" />}
								{country === name ? (
									<path
										d="M12 21.7
            C17.3 17 20 10 20 10
            a9 9 0 1 0-16 0
            c1 3 3.5 8.1 8 11.7
            z"
									/>
								) : (
									<path d="M12 21.7C17.3 17 20 13 20 10a8 8 0 1 0-16 0c0 3 2.7 6.9 8 11.7z" />
								)}
							</g>
						</Marker>
					))}
				</ComposableMap>
			</div>
		);
	}
}
