import React, { Component } from 'react';

export default class ArrowSpinner extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
			hover: false,
		};
		this.toggleHover = this.toggleHover.bind(this);
	}

	componentDidMount() {
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);
		/* 		this.setState({ hover: false}) */
	}

	toggleHover() {
		this.setState({ hover: !this.state.hover });
	}

	componentWillUnmount() {
		this.setState({ hover: false });
	}

	render() {
		let linkStyle;
		if (this.state.hover) {
			linkStyle = { backgroundColor: this.props.color };
		} else {
			linkStyle = { backgroundColor: '#ffffff1a' };
		}
		return (
			<div className="arrow_spinner" key={linkStyle} onClick={this.props.next} onMouseEnter={this.toggleHover}
			onMouseLeave={this.toggleHover}>
				<img className="arrow_spinner_icon" src="/assets/images/icons/arrow_white_right.png" alt="Next" />
				<button
					className="arrow_spinner_btn"
					style={linkStyle}
					onMouseEnter={this.toggleHover}
					onMouseLeave={this.toggleHover}
				>
					<svg viewBox="0 0 100 100">
						<path
							className="arrow_spinner_stroke"
							d="M 50,50 m 0,-48 a 48,48 0 1 1 0,96 a 48,48 0 1 1 0,-96"
							stroke="#fff"
							strokeWidth="4"
							fill-opacity="0"
							stroke-dasharray="301.635, 301.635"
							stroke-dashoffset={this.props.time}
						></path>
					</svg>
				</button>
			</div>
		);
	}
}
