import React, { Component } from 'react';
import Arrow_Spinner from '../General/Arrow_Spinner';

class IntroSlides extends Component {
	constructor(props) {
		super(props);
		this.state = {
			didMount: false,
			time: 300,
			slide2: false,
			slide3: false,
		};
	}

	componentDidMount() {
		setTimeout(() => {
			this.setState({ didMount: true });
		}, 0);

		this.myInterval = setInterval(() => {
			this.setState(({ time }) => ({
				time: time - 1,
			}));
			if (this.state.time === 0 && this.props.intro.id === 3) {
				clearInterval(this.myInterval);
			} 

			else if (this.state.time === -605) {
				clearInterval(this.myInterval);
			}
		}, 30);
	}

	componentDidUpdate() {
		if (this.props.intro.id === 2) {
			this.setState({slide2: true})
		} else if (this.props.intro.id === 3) {
			this.setState({slide3: true, slide2: false})
		}
	}

	componentWillUnmount() {
		clearInterval(this.myInterval);
	}

	render() {
		console.log(this.state.time);
		const intro = this.props.intro;
		return (
			<div id="introSlides">
				<div className="background">
					<div className="background_color" style={{ backgroundColor: this.props.intros[0].color }}></div>
					<div className={`${this.state.slide2 && 'fade-in'} background_color`}
							style={{ backgroundColor: this.props.intros[1].color }}></div>
					<div className={`${this.state.slide3 && 'fade-in'} background_color`} style={{ backgroundColor: this.props.intros[2].color }}></div>
				</div>
				{intro.id === 1 || 3 ? (
					<div className="text">
						<h5 className="tracking-in-expand-fwd-top">{intro.subtitle}.</h5>
						<h1 className="tracking-in-expand-fwd-top">{intro.title}</h1>
					</div>
				) : (
					<div className="text">
						<h5 className="tracking-in-expand-fwd-bottom">{intro.subtitle}.</h5>
						<h1 className="tracking-in-expand-fwd-bottom">{intro.title}</h1>
					</div>
				)}
				<div className="image">
					<img className="image_sliding" src={intro.imageUrl} alt="Profile_Picture" />
					<div className="image_control">
						<div className="image_control_text">
							<p>01</p>
							<p>/</p>
							<p>03</p>
						</div>
						<Arrow_Spinner time={this.state.time} />
					</div>
				</div>
			</div>
		);
	}
}

export default IntroSlides;
